import React, { useState } from 'react';
import axios from 'axios';

interface LoginResponse {
    // 根据你的API返回的响应数据结构进行调整
    token?: string;
    [key: string]: any;
  }
const LoginForm: React.FC = () => {
  const [username, setUsername] = useState('17710831560');
  const [password, setPassword] = useState('D/Lm4fqE+9IIUpj0VO/J9A==');
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
    var _cookies:string[]=[];
  const handleLogin = async () => {
    try {
        const loginResponse = await axios.post<LoginResponse>(
            'https://wx.rocketbird.cn/Web/Public/check_login', // 登录URL
            new URLSearchParams({ username: username, password: password }), // 表单数据
            {
              headers: {
                'Content-Type': 'application/x-www-form-urlencoded',
              },
              withCredentials: true, // 确保请求包含和接受Cookies
            }
          );
      
          // 检查登录响应
          if (loginResponse.status === 200) {
            console.log('登录成功');
            var parma={
                search:'',
                begin_date:'2024-06-1',
                end_date:'2024-06-13',
                page_no:'1',
                page_size:'0',
                type:'',
                pay_type:'',
                pay_status:'',
                related:'',
                bus_id:'11536'
            }
            // 使用登录时获得的Cookies发送后续请求
            const response = await axios.post('https://wx.rocketbird.cn/Web/statistics/ReceiveLogList', parma,{
              withCredentials: true, // 确保请求包含和接受Cookies
            });
      
            console.log('后续请求数据:', response.data);
          } else {
            console.error('登录失败:', loginResponse.statusText);
          }
    } catch (error) {
      console.error('Login error', error);
    }
  };

  const handleSearch = async () => {
    
    var parma={
        search:'',
        begin_date:'2024-06-1',
        end_date:'2024-06-13',
        page_no:'1',
        page_size:'0',
        type:'',
        pay_type:'',
        pay_status:'',
        related:'',
        bus_id:'11536'
    }
    var cookies=_cookies.join('; ');
    try {
      const response = await axios.post('http://localhost:3001/api/search', {parma,cookies}, { withCredentials: true });
      setResults(response.data.results);
    } catch (error) {
      console.error('Search error', error);
    }
  };

  return (
    <div>
      <h1>Login</h1>
      <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleLogin}>Login</button>
      
      <h1>Search</h1>
      <input type="text" placeholder="Search query" value={query} onChange={(e) => setQuery(e.target.value)} />
      <button onClick={handleSearch}>Search</button>

      <h2>Results</h2>
      <ul>
        {results.map((result, index) => (
          <li key={index}>{result}</li>
        ))}
      </ul>
    </div>
  );
};

export default LoginForm;
