import React, { useState } from 'react';
import axios from 'axios';

const LoginForm: React.FC = () => {
  const [username, setUsername] = useState('17710831560');
  const [password, setPassword] = useState('D/Lm4fqE+9IIUpj0VO/J9A==');
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleLogin = async () => {
    try {
        const formData = new URLSearchParams();
    formData.append('username', username);
    formData.append('password', password);
    formData.append('vCode', '');

    const response = await fetch('https://wx.rocketbird.cn/Web/Public/check_login', {
      method: 'POST',
      body: formData,
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
        'Connection': 'keep-alive',
        'Accept-Encoding': 'gzip, deflate, br',
        'Accept': '*/*',
      },
      credentials: 'include' // 允许跨域请求携带cookie
    });

    if (!response.ok) {
      throw new Error('Network response was not ok');
    }

    const loginResponseHeaders = response.headers;
    const setCookieHeader = loginResponseHeaders.get('set-cookie');

    if (setCookieHeader) {
      const cookies = setCookieHeader.split(','); // 如果有多个Set-Cookie头部，需要拆分
      cookies.forEach((cookie) => {
        const [cookieName, ...cookieValue] = cookie.split(';')[0].split('=');
        console.log(cookieName, cookieValue.join('='));
      });
    } else {
      console.log('No set-cookie header found');
    }

    const responseData = await response.json();
    console.log('Login successful', responseData);
  } catch (error) {
    console.error('Login error', error);
  }
  };

  const handleSearch = async () => {
    
    var parma={
        search:'',
        begin_date:'2024-06-1',
        end_date:'2024-06-13',
        page_no:1,
        page_size:0,
        type:'',
        pay_type:'',
        pay_status:'',
        related:'',
        bus_id:11536
    }
    try {
      const response = await axios.post('http://localhost:3001/api/search', {parma}, { withCredentials: false });
      setResults(response.data.results);
    } catch (error) {
      console.error('Search error', error);
    }
  };

  return (
    <div>
      <h1>Login</h1>
      <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleLogin}>Login</button>
      
      <h1>Search</h1>
      <input type="text" placeholder="Search query" value={query} onChange={(e) => setQuery(e.target.value)} />
      <button onClick={handleSearch}>Search</button>

      <h2>Results</h2>
      <ul>
        {results.map((result, index) => (
          <li key={index}>{result}</li>
        ))}
      </ul>
    </div>
  );
};

export default LoginForm;
