import React, { useState } from 'react';
import axios from 'axios';

const LoginForm: React.FC = () => {
  const [username, setUsername] = useState('17710831560');
  const [password, setPassword] = useState('D/Lm4fqE+9IIUpj0VO/J9A==');
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleLogin = async () => {
    try {
        const loginResponse = await axios.post('https://wx.rocketbird.cn/Web/Public/check_login', { username:username, password:password,vCode:'' },{
            withCredentials: true,
            headers: {
                'Content-Type': 'application/json',
                'Connection': 'keep-alive',
                'Accept-Encoding': 'gzip, deflate, br',
                'Accept': '*/*'
              }
        });
        console.log(loginResponse.headers)
        const cookies = loginResponse.headers['set-cookie'];
        if (cookies) {
            cookies.forEach((cookie) => {
                const [cookieName, ...cookieValue] = cookie.split(';')[0].split('=');
                //res.cookie(cookieName, cookieValue.join('='), { httpOnly: cookieName==='acw_tc' });
                console.log(cookieName,cookieValue.join('='))
            });
        }
    } catch (error) {
      console.error('Login error', error);
    }
  };

  const handleSearch = async () => {
    
    var parma={
        search:'',
        begin_date:'2024-06-1',
        end_date:'2024-06-13',
        page_no:1,
        page_size:0,
        type:'',
        pay_type:'',
        pay_status:'',
        related:'',
        bus_id:11536
    }
    try {
      const response = await axios.post('http://localhost:3001/api/search', {parma}, { withCredentials: false });
      setResults(response.data.results);
    } catch (error) {
      console.error('Search error', error);
    }
  };

  return (
    <div>
      <h1>Login</h1>
      <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleLogin}>Login</button>
      
      <h1>Search</h1>
      <input type="text" placeholder="Search query" value={query} onChange={(e) => setQuery(e.target.value)} />
      <button onClick={handleSearch}>Search</button>

      <h2>Results</h2>
      <ul>
        {results.map((result, index) => (
          <li key={index}>{result}</li>
        ))}
      </ul>
    </div>
  );
};

export default LoginForm;
