import React, { useState } from 'react';
import axios from 'axios';

const LoginForm: React.FC = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:3001/api/login', { username, password }, { withCredentials: false });
      console.log('Login successful', response);
    } catch (error) {
      console.error('Login error', error);
    }
  };

  const handleSearch = async () => {
    
    var parma={
        search:'',
        begin_date:'2024-06-1',
        end_date:'2024-06-13',
        page_no:1,
        page_size:0,
        type:'',
        pay_type:'',
        pay_status:'',
        related:'',
        bus_id:11536
    }
    try {
      const response = await axios.post('http://localhost:3001/api/search', { parma }, { withCredentials: false });
      setResults(response.data.results);
    } catch (error) {
      console.error('Search error', error);
    }
  };

  return (
    <div>
      <h1>Login</h1>
      <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} />
      <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} />
      <button onClick={handleLogin}>Login</button>
      
      <h1>Search</h1>
      <input type="text" placeholder="Search query" value={query} onChange={(e) => setQuery(e.target.value)} />
      <button onClick={handleSearch}>Search</button>

      <h2>Results</h2>
      <ul>
        {results.map((result, index) => (
          <li key={index}>{result}</li>
        ))}
      </ul>
    </div>
  );
};

export default LoginForm;
